mapper.insert:
insert into recode (id, uid, valueM, 
  valueA, valueN, theWek, 
  theDay, recodeCreate, recodeModified
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{valueM,jdbcType=VARCHAR}, 
  #{valueA,jdbcType=VARCHAR}, #{valueN,jdbcType=VARCHAR}, #{theWek,jdbcType=VARCHAR}, 
  #{theDay,jdbcType=VARCHAR}, #{recodeCreate,jdbcType=TIMESTAMP}, #{recodeModified,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT u.userName,valueM,valueA,valueN,theDay,theWek FROM recode r join `user` u on r.uid = u.id
QUARTER (theDay) = #{quarter} and YEAR(theDay) = #{year}
and userName like '${userName}'

SELECT u.userName,valueM,valueA,valueN,theDay,theWek FROM recode r join `user` u on r.uid = u.id
YEAR(theDay) = #{year}
and userName like '${userName}'
